package PatternPrograms;

public class IncreasingTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=5;
		
		for(int i=1; i<=n; i++) { //rows
			
			for(int j=1; j<=i; j++) {
				
			System.out.print("*");
			
		}
			System.out.println();
	}

}
}
