package PatternPrograms;

public class NewPattern4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=5;
		
		int k=0;
		
		for(int i=0; i<n; i++) {
			
			for(int j=0; j<n; j++) {
				k++;
				System.out.print(k + " ");
			}
			
			System.out.println();

		}
		
	}

}
